SELECT nation.n_name, VARIANCE(nation.n_regionkey) AS variance_nation_n_regionkey, AVG(nation.n_nationkey) AS avg_nation_n_nationkey, AVG(nation.n_regionkey) AS avg_nation_n_regionkey, STDDEV(nation.n_nationkey) AS stddev_nation_n_nationkey FROM nation WHERE (nation.n_name LIKE 'A%') OR (nation.n_nationkey > 313) AND nation.n_comment = 'JARKQ' GROUP BY nation.n_name ORDER BY nation.n_name