SELECT orders.o_orderpriority, SUM(orders.o_totalprice) AS sum_orders_o_totalprice, COUNT(orders.o_totalprice) AS count_orders_o_totalprice, SUM(orders.o_custkey) AS sum_orders_o_custkey, COUNT(orders.o_orderkey) AS count_orders_o_orderkey FROM orders WHERE (orders.o_custkey < 959 AND orders.o_orderpriority = 'FNLIT' AND orders.o_orderkey BETWEEN 894 AND 900) OR (orders.o_shippriority = 895) GROUP BY orders.o_orderpriority HAVING COUNT(*) > 4474 AND SUM(orders.o_custkey) > 55982 ORDER BY orders.o_orderpriority