SELECT customer.c_mktsegment, customer.c_name, customer.c_address, AVG(customer.c_custkey) AS avg_customer_c_custkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal, VARIANCE(customer.c_custkey) AS variance_customer_c_custkey, AVG(customer.c_nationkey) AS avg_customer_c_nationkey FROM customer WHERE customer.c_address = 'CFVOT' GROUP BY customer.c_mktsegment, customer.c_name, customer.c_address ORDER BY customer.c_mktsegment, customer.c_name