SELECT part.p_mfgr, part.p_container, SUM(part.p_retailprice) AS sum_part_p_retailprice, SUM(part.p_retailprice) AS sum_part_p_retailprice, SUM(part.p_retailprice) AS sum_part_p_retailprice FROM part WHERE (part.p_mfgr = 'KJUZY') OR (part.p_mfgr = 'XPSJM') AND part.p_type LIKE 'A%' AND part.p_partkey BETWEEN 606 AND 707 GROUP BY part.p_mfgr, part.p_container ORDER BY part.p_container, part.p_mfgr