SELECT supplier.s_comment, supplier.s_address, VARIANCE(supplier.s_suppkey) AS variance_supplier_s_suppkey, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal FROM supplier WHERE supplier.s_name = 'KDVBW' GROUP BY supplier.s_comment, supplier.s_address ORDER BY supplier.s_address, supplier.s_comment