SELECT nation.n_name, nation.n_comment, COUNT(nation.n_nationkey) AS count_nation_n_nationkey, AVG(nation.n_regionkey) AS avg_nation_n_regionkey FROM nation WHERE (nation.n_regionkey BETWEEN 644 AND 652) OR (nation.n_comment LIKE 'A%') AND nation.n_comment LIKE '%C' GROUP BY nation.n_name, nation.n_comment HAVING COUNT(*) > 6850 AND AVG(nation.n_regionkey) > 77467 ORDER BY nation.n_comment, nation.n_name