SELECT customer.c_phone, customer.c_name, customer.c_mktsegment, STDDEV(customer.c_nationkey) AS stddev_customer_c_nationkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal FROM customer WHERE (customer.c_nationkey BETWEEN 422 AND 658 AND customer.c_custkey > 226 AND customer.c_phone = 'XLPKB') OR (customer.c_address LIKE '%B%') GROUP BY customer.c_phone, customer.c_name, customer.c_mktsegment ORDER BY customer.c_phone, customer.c_name