SELECT supplier.s_name, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey, AVG(supplier.s_acctbal) AS avg_supplier_s_acctbal, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey, COUNT(supplier.s_nationkey) AS count_supplier_s_nationkey, VARIANCE(supplier.s_nationkey) AS variance_supplier_s_nationkey FROM supplier WHERE (supplier.s_phone LIKE '%B%') OR (supplier.s_comment LIKE '%C') AND supplier.s_comment = 'IOMTU' GROUP BY supplier.s_name ORDER BY supplier.s_name