SELECT customer.c_address, COUNT(customer.c_nationkey) AS count_customer_c_nationkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, STDDEV(customer.c_acctbal) AS stddev_customer_c_acctbal FROM customer WHERE ((customer.c_nationkey < 774 AND customer.c_nationkey < 302) OR (customer.c_mktsegment = 'PDVAO')) OR (customer.c_name = 'VUTCW') GROUP BY customer.c_address HAVING COUNT(*) > 6535 AND VARIANCE(customer.c_nationkey) > 80280 ORDER BY customer.c_address