SELECT lineitem.l_shipdate, MAX(lineitem.l_linenumber) AS max_lineitem_l_linenumber, COUNT(lineitem.l_extendedprice) AS count_lineitem_l_extendedprice, AVG(lineitem.l_partkey) AS avg_lineitem_l_partkey, MIN(lineitem.l_partkey) AS min_lineitem_l_partkey, COUNT(lineitem.l_tax) AS count_lineitem_l_tax FROM lineitem WHERE lineitem.l_comment = 'IGJYB' GROUP BY lineitem.l_shipdate ORDER BY lineitem.l_shipdate