SELECT orders.o_orderpriority, orders.o_orderstatus, AVG(orders.o_custkey) AS avg_orders_o_custkey, VARIANCE(orders.o_orderkey) AS variance_orders_o_orderkey FROM orders WHERE (orders.o_orderpriority LIKE '%B%') OR (orders.o_comment = 'QLTRH') GROUP BY orders.o_orderpriority, orders.o_orderstatus HAVING COUNT(*) > 8698 AND VARIANCE(orders.o_orderkey) > 50670 ORDER BY orders.o_orderstatus, orders.o_orderpriority