SELECT region.r_name, region.r_comment, SUM(region.r_regionkey) AS sum_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey FROM region WHERE ((region.r_regionkey BETWEEN 32 AND 485 AND region.r_name = 'IMZYK') OR (region.r_name LIKE 'A%')) OR (region.r_regionkey < 345) GROUP BY region.r_name, region.r_comment HAVING COUNT(*) > 1749 AND SUM(region.r_regionkey) > 10888 ORDER BY region.r_name, region.r_comment