SELECT region.r_name, SUM(region.r_regionkey) AS sum_region_r_regionkey, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey FROM region WHERE (((region.r_comment = 'GNMBP') OR (region.r_regionkey = 722)) OR (region.r_comment = 'JKMUX')) OR (region.r_comment LIKE '%B%') GROUP BY region.r_name HAVING COUNT(*) > 9759 AND STDDEV(region.r_regionkey) > 61615 ORDER BY region.r_name