SELECT part.p_mfgr, part.p_brand, part.p_comment, MIN(part.p_size) AS min_part_p_size, AVG(part.p_size) AS avg_part_p_size, SUM(part.p_retailprice) AS sum_part_p_retailprice, SUM(part.p_partkey) AS sum_part_p_partkey FROM part WHERE part.p_comment = 'RDDFL' GROUP BY part.p_mfgr, part.p_brand, part.p_comment HAVING COUNT(*) > 4834 AND MIN(part.p_size) > 19667 ORDER BY part.p_brand, part.p_mfgr