SELECT customer.c_phone, customer.c_name, AVG(customer.c_custkey) AS avg_customer_c_custkey, COUNT(customer.c_nationkey) AS count_customer_c_nationkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal, MIN(customer.c_custkey) AS min_customer_c_custkey FROM customer WHERE customer.c_nationkey > 852 AND customer.c_mktsegment LIKE '%C' GROUP BY customer.c_phone, customer.c_name HAVING COUNT(*) > 7689 AND MIN(customer.c_custkey) > 87561 ORDER BY customer.c_phone, customer.c_name