SELECT lineitem.l_shipmode, lineitem.l_shipdate, STDDEV(lineitem.l_linenumber) AS stddev_lineitem_l_linenumber, AVG(lineitem.l_partkey) AS avg_lineitem_l_partkey, MAX(lineitem.l_tax) AS max_lineitem_l_tax, VARIANCE(lineitem.l_quantity) AS variance_lineitem_l_quantity FROM lineitem WHERE lineitem.l_shipdate >= '2020-10-23' GROUP BY lineitem.l_shipmode, lineitem.l_shipdate ORDER BY lineitem.l_shipdate, lineitem.l_shipmode