SELECT partsupp.ps_comment, SUM(partsupp.ps_supplycost) AS sum_partsupp_ps_supplycost, AVG(partsupp.ps_availqty) AS avg_partsupp_ps_availqty, MIN(partsupp.ps_availqty) AS min_partsupp_ps_availqty, SUM(partsupp.ps_supplycost) AS sum_partsupp_ps_supplycost, SUM(partsupp.ps_availqty) AS sum_partsupp_ps_availqty FROM partsupp WHERE partsupp.ps_suppkey = 406 AND partsupp.ps_partkey BETWEEN 483 AND 718 GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment