SELECT partsupp.ps_comment, AVG(partsupp.ps_availqty) AS avg_partsupp_ps_availqty, SUM(partsupp.ps_availqty) AS sum_partsupp_ps_availqty, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost, SUM(partsupp.ps_suppkey) AS sum_partsupp_ps_suppkey, COUNT(partsupp.ps_availqty) AS count_partsupp_ps_availqty FROM partsupp WHERE partsupp.ps_suppkey < 128 GROUP BY partsupp.ps_comment HAVING COUNT(*) > 5535 AND SUM(partsupp.ps_availqty) > 28340 ORDER BY partsupp.ps_comment