SELECT orders.o_comment, orders.o_clerk, orders.o_orderstatus, MAX(orders.o_orderkey) AS max_orders_o_orderkey, MIN(orders.o_shippriority) AS min_orders_o_shippriority, AVG(orders.o_orderkey) AS avg_orders_o_orderkey FROM orders WHERE orders.o_clerk = 'QTDEW' GROUP BY orders.o_comment, orders.o_clerk, orders.o_orderstatus HAVING COUNT(*) > 7642 AND MIN(orders.o_shippriority) > 61252 ORDER BY orders.o_orderstatus, orders.o_comment