SELECT nation.n_comment, nation.n_name, MIN(nation.n_nationkey) AS min_nation_n_nationkey, STDDEV(nation.n_nationkey) AS stddev_nation_n_nationkey, STDDEV(nation.n_regionkey) AS stddev_nation_n_regionkey FROM nation WHERE (nation.n_comment LIKE '%C' AND nation.n_nationkey BETWEEN 198 AND 717) OR (nation.n_comment LIKE 'A%') AND nation.n_nationkey > 95 GROUP BY nation.n_comment, nation.n_name ORDER BY nation.n_comment, nation.n_name