SELECT supplier.s_phone, supplier.s_name, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal, VARIANCE(supplier.s_suppkey) AS variance_supplier_s_suppkey FROM supplier WHERE (supplier.s_name = 'ORJIC' AND supplier.s_suppkey = 599) OR (supplier.s_address = 'CRYIJ') GROUP BY supplier.s_phone, supplier.s_name HAVING COUNT(*) > 6946 AND MIN(supplier.s_acctbal) > 62215 ORDER BY supplier.s_phone, supplier.s_name