SELECT supplier.s_comment, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey, VARIANCE(supplier.s_nationkey) AS variance_supplier_s_nationkey FROM supplier WHERE ((supplier.s_name = 'YDWTJ') OR (supplier.s_nationkey = 310)) OR (supplier.s_phone = 'TZOYY') GROUP BY supplier.s_comment ORDER BY supplier.s_comment