SELECT nation.n_comment, SUM(nation.n_nationkey) AS sum_nation_n_nationkey, MAX(nation.n_regionkey) AS max_nation_n_regionkey, MIN(nation.n_regionkey) AS min_nation_n_regionkey, STDDEV(nation.n_regionkey) AS stddev_nation_n_regionkey FROM nation WHERE (nation.n_comment LIKE '%B%' AND nation.n_comment LIKE '%C' AND nation.n_regionkey BETWEEN 151 AND 343) OR (nation.n_name LIKE '%C') GROUP BY nation.n_comment ORDER BY nation.n_comment