SELECT customer.c_name, customer.c_comment, AVG(customer.c_custkey) AS avg_customer_c_custkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal, SUM(customer.c_custkey) AS sum_customer_c_custkey FROM customer WHERE customer.c_nationkey > 80 AND customer.c_address = 'YGMKL' AND customer.c_custkey < 88 GROUP BY customer.c_name, customer.c_comment ORDER BY customer.c_comment, customer.c_name