SELECT nation.n_name, MIN(nation.n_regionkey) AS min_nation_n_regionkey, MIN(nation.n_nationkey) AS min_nation_n_nationkey, AVG(nation.n_regionkey) AS avg_nation_n_regionkey, COUNT(nation.n_nationkey) AS count_nation_n_nationkey, MAX(nation.n_regionkey) AS max_nation_n_regionkey FROM nation WHERE nation.n_comment LIKE '%C' AND nation.n_comment LIKE '%C' GROUP BY nation.n_name ORDER BY nation.n_name