SELECT region.r_name, MIN(region.r_regionkey) AS min_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, COUNT(region.r_regionkey) AS count_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey FROM region WHERE (region.r_regionkey < 309) OR (region.r_regionkey > 82) GROUP BY region.r_name HAVING COUNT(*) > 5143 AND MIN(region.r_regionkey) > 94491 ORDER BY region.r_name