SELECT customer.c_phone, SUM(customer.c_acctbal) AS sum_customer_c_acctbal, COUNT(customer.c_nationkey) AS count_customer_c_nationkey, AVG(customer.c_custkey) AS avg_customer_c_custkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal FROM customer WHERE (customer.c_nationkey BETWEEN 183 AND 476 AND customer.c_mktsegment LIKE '%B%') OR (customer.c_custkey BETWEEN 710 AND 880) GROUP BY customer.c_phone HAVING COUNT(*) > 3050 AND SUM(customer.c_acctbal) > 36465 ORDER BY customer.c_phone