SELECT customer.c_address, COUNT(customer.c_acctbal) AS count_customer_c_acctbal, VARIANCE(customer.c_acctbal) AS variance_customer_c_acctbal, AVG(customer.c_nationkey) AS avg_customer_c_nationkey FROM customer WHERE (customer.c_address = 'KAZPT' AND customer.c_comment LIKE '%B%' AND customer.c_custkey BETWEEN 454 AND 821) OR (customer.c_name LIKE '%C') GROUP BY customer.c_address HAVING COUNT(*) > 2427 AND COUNT(customer.c_acctbal) > 42286 ORDER BY customer.c_address