SELECT lineitem.l_shipmode, lineitem.l_receiptdate, lineitem.l_commitdate, STDDEV(lineitem.l_partkey) AS stddev_lineitem_l_partkey, AVG(lineitem.l_discount) AS avg_lineitem_l_discount, STDDEV(lineitem.l_linenumber) AS stddev_lineitem_l_linenumber, VARIANCE(lineitem.l_quantity) AS variance_lineitem_l_quantity, STDDEV(lineitem.l_partkey) AS stddev_lineitem_l_partkey FROM lineitem WHERE lineitem.l_linestatus LIKE 'A%' GROUP BY lineitem.l_shipmode, lineitem.l_receiptdate, lineitem.l_commitdate HAVING COUNT(*) > 1043 AND STDDEV(lineitem.l_linenumber) > 69781 ORDER BY lineitem.l_shipmode, lineitem.l_commitdate