SELECT region.r_name, SUM(region.r_regionkey) AS sum_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey FROM region WHERE (region.r_comment = 'CONZJ') OR (region.r_name = 'NIYTS') GROUP BY region.r_name HAVING COUNT(*) > 8001 AND SUM(region.r_regionkey) > 50646 ORDER BY region.r_name