SELECT partsupp.ps_comment, MIN(partsupp.ps_availqty) AS min_partsupp_ps_availqty, COUNT(partsupp.ps_suppkey) AS count_partsupp_ps_suppkey, MAX(partsupp.ps_partkey) AS max_partsupp_ps_partkey, MAX(partsupp.ps_partkey) AS max_partsupp_ps_partkey FROM partsupp WHERE partsupp.ps_comment = 'SVIVG' AND partsupp.ps_availqty < 617 GROUP BY partsupp.ps_comment HAVING COUNT(*) > 7846 AND MIN(partsupp.ps_availqty) > 6729 ORDER BY partsupp.ps_comment