SELECT lineitem.l_returnflag, AVG(lineitem.l_orderkey) AS avg_l_orderkey, AVG(lineitem.l_partkey) AS avg_l_partkey, COUNT(lineitem.l_partkey) AS count_l_partkey FROM lineitem WHERE lineitem.l_suppkey = 755 AND lineitem.l_partkey BETWEEN 694 AND 860 AND lineitem.l_commitdate >= '2005-01-21' AND lineitem.l_returnflag = 'AXDJL' GROUP BY lineitem.l_returnflag HAVING COUNT(*) > 641 ORDER BY lineitem.l_returnflag