SELECT lineitem.l_extendedprice, COUNT(lineitem.l_partkey) AS count_l_partkey, AVG(lineitem.l_linenumber) AS avg_l_linenumber, MAX(lineitem.l_suppkey) AS max_l_suppkey FROM lineitem WHERE lineitem.l_shipinstruct = 'WOWWE' AND lineitem.l_shipinstruct = 'IOLTM' AND lineitem.l_receiptdate >= '1997-08-16' AND lineitem.l_receiptdate >= '2021-04-13' AND lineitem.l_orderkey > 783 GROUP BY lineitem.l_extendedprice ORDER BY lineitem.l_extendedprice