SELECT nation.n_comment, nation.n_name, STDDEV(nation.n_regionkey) AS stddev_nation_n_regionkey, AVG(nation.n_nationkey) AS avg_nation_n_nationkey, MIN(nation.n_regionkey) AS min_nation_n_regionkey, AVG(nation.n_regionkey) AS avg_nation_n_regionkey, MIN(nation.n_regionkey) AS min_nation_n_regionkey FROM nation WHERE nation.n_nationkey = 286 AND nation.n_nationkey > 835 GROUP BY nation.n_comment, nation.n_name HAVING COUNT(*) > 8898 AND STDDEV(nation.n_regionkey) > 3322 ORDER BY nation.n_name, nation.n_comment