SELECT orders.o_orderstatus, orders.o_orderdate, COUNT(orders.o_custkey) AS count_orders_o_custkey, COUNT(orders.o_custkey) AS count_orders_o_custkey, SUM(orders.o_shippriority) AS sum_orders_o_shippriority, MIN(orders.o_shippriority) AS min_orders_o_shippriority, AVG(orders.o_custkey) AS avg_orders_o_custkey FROM orders WHERE (((orders.o_clerk LIKE 'A%') OR (orders.o_clerk LIKE '%B%')) OR (orders.o_orderstatus = 'HBYWW')) OR (orders.o_comment LIKE '%C') GROUP BY orders.o_orderstatus, orders.o_orderdate ORDER BY orders.o_orderstatus, orders.o_orderdate