SELECT region.r_comment, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey FROM region WHERE (region.r_comment = 'JFLZJ' AND region.r_comment LIKE 'A%' AND region.r_regionkey BETWEEN 132 AND 137) OR (region.r_name = 'ZFICF') GROUP BY region.r_comment ORDER BY region.r_comment