SELECT partsupp.ps_comment, COUNT(partsupp.ps_supplycost) AS count_partsupp_ps_supplycost, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost, MAX(partsupp.ps_availqty) AS max_partsupp_ps_availqty, STDDEV(partsupp.ps_availqty) AS stddev_partsupp_ps_availqty, STDDEV(partsupp.ps_availqty) AS stddev_partsupp_ps_availqty FROM partsupp WHERE (partsupp.ps_comment LIKE 'A%') OR (partsupp.ps_partkey < 488) GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment