SELECT customer.c_phone, STDDEV(customer.c_acctbal) AS stddev_customer_c_acctbal, AVG(customer.c_custkey) AS avg_customer_c_custkey, STDDEV(customer.c_custkey) AS stddev_customer_c_custkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey FROM customer WHERE customer.c_custkey < 486 GROUP BY customer.c_phone ORDER BY customer.c_phone