SELECT supplier.s_address, AVG(supplier.s_acctbal) AS avg_supplier_s_acctbal, MIN(supplier.s_nationkey) AS min_supplier_s_nationkey, AVG(supplier.s_acctbal) AS avg_supplier_s_acctbal, STDDEV(supplier.s_nationkey) AS stddev_supplier_s_nationkey, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal FROM supplier GROUP BY supplier.s_address HAVING COUNT(*) > 5364 AND COUNT(supplier.s_acctbal) > 14447 ORDER BY supplier.s_address