SELECT supplier.s_name, supplier.s_phone, VARIANCE(supplier.s_acctbal) AS variance_supplier_s_acctbal, MIN(supplier.s_nationkey) AS min_supplier_s_nationkey FROM supplier WHERE ((supplier.s_nationkey BETWEEN 51 AND 262) OR (supplier.s_phone LIKE 'A%')) OR (supplier.s_address LIKE 'A%') GROUP BY supplier.s_name, supplier.s_phone HAVING COUNT(*) > 4698 AND MIN(supplier.s_nationkey) > 85993 ORDER BY supplier.s_phone, supplier.s_name