SELECT nation.n_comment, AVG(nation.n_nationkey) AS avg_nation_n_nationkey, SUM(nation.n_regionkey) AS sum_nation_n_regionkey, STDDEV(nation.n_regionkey) AS stddev_nation_n_regionkey, VARIANCE(nation.n_nationkey) AS variance_nation_n_nationkey, STDDEV(nation.n_regionkey) AS stddev_nation_n_regionkey FROM nation WHERE nation.n_comment = 'TDOUC' AND nation.n_name LIKE '%B%' GROUP BY nation.n_comment HAVING COUNT(*) > 5263 AND STDDEV(nation.n_regionkey) > 15994 ORDER BY nation.n_comment