SELECT customer.c_name, customer.c_mktsegment, SUM(customer.c_nationkey) AS sum_customer_c_nationkey, MAX(customer.c_custkey) AS max_customer_c_custkey, VARIANCE(customer.c_custkey) AS variance_customer_c_custkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal FROM customer WHERE customer.c_custkey BETWEEN 105 AND 464 GROUP BY customer.c_name, customer.c_mktsegment ORDER BY customer.c_mktsegment, customer.c_name