SELECT supplier.s_name, supplier.s_phone, AVG(supplier.s_nationkey) AS avg_supplier_s_nationkey, AVG(supplier.s_suppkey) AS avg_supplier_s_suppkey, STDDEV(supplier.s_suppkey) AS stddev_supplier_s_suppkey FROM supplier WHERE (supplier.s_comment = 'JQSQJ') OR (supplier.s_nationkey = 86) GROUP BY supplier.s_name, supplier.s_phone HAVING COUNT(*) > 5204 AND AVG(supplier.s_nationkey) > 2927 ORDER BY supplier.s_name, supplier.s_phone