SELECT nation.n_comment, nation.n_name, VARIANCE(nation.n_nationkey) AS variance_nation_n_nationkey, COUNT(nation.n_regionkey) AS count_nation_n_regionkey FROM nation WHERE ((nation.n_name = 'MAGGB' AND nation.n_name LIKE '%B%') OR (nation.n_name LIKE 'A%')) OR (nation.n_comment = 'DEXHC') GROUP BY nation.n_comment, nation.n_name HAVING COUNT(*) > 7408 AND COUNT(nation.n_regionkey) > 23731 ORDER BY nation.n_name, nation.n_comment