SELECT orders.o_orderpriority, STDDEV(orders.o_shippriority) AS stddev_orders_o_shippriority, AVG(orders.o_custkey) AS avg_orders_o_custkey, MAX(orders.o_orderkey) AS max_orders_o_orderkey, STDDEV(orders.o_orderkey) AS stddev_orders_o_orderkey, AVG(orders.o_custkey) AS avg_orders_o_custkey FROM orders WHERE (orders.o_custkey BETWEEN 732 AND 863 AND orders.o_shippriority < 859) OR (orders.o_shippriority > 287) AND orders.o_shippriority BETWEEN 439 AND 908 GROUP BY orders.o_orderpriority ORDER BY orders.o_orderpriority