SELECT supplier.s_comment, supplier.s_address, MAX(supplier.s_acctbal) AS max_supplier_s_acctbal, AVG(supplier.s_acctbal) AS avg_supplier_s_acctbal, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey FROM supplier WHERE supplier.s_name = 'LCJAK' AND supplier.s_phone LIKE 'A%' AND supplier.s_nationkey BETWEEN 807 AND 983 GROUP BY supplier.s_comment, supplier.s_address HAVING COUNT(*) > 3375 AND AVG(supplier.s_acctbal) > 5156 ORDER BY supplier.s_comment, supplier.s_address