SELECT supplier.s_comment, supplier.s_address, COUNT(supplier.s_acctbal) AS count_supplier_s_acctbal, MIN(supplier.s_suppkey) AS min_supplier_s_suppkey, COUNT(supplier.s_nationkey) AS count_supplier_s_nationkey FROM supplier WHERE supplier.s_address LIKE 'A%' AND supplier.s_nationkey < 758 GROUP BY supplier.s_comment, supplier.s_address HAVING COUNT(*) > 3672 AND COUNT(supplier.s_acctbal) > 42423 ORDER BY supplier.s_address, supplier.s_comment