SELECT orders.o_orderdate, MIN(orders.o_custkey) AS min_orders_o_custkey, SUM(orders.o_shippriority) AS sum_orders_o_shippriority, SUM(orders.o_orderkey) AS sum_orders_o_orderkey, STDDEV(orders.o_orderkey) AS stddev_orders_o_orderkey FROM orders WHERE (orders.o_custkey BETWEEN 150 AND 189) OR (orders.o_clerk LIKE '%C') AND orders.o_custkey > 363 GROUP BY orders.o_orderdate HAVING COUNT(*) > 9592 AND SUM(orders.o_shippriority) > 29840 ORDER BY orders.o_orderdate