SELECT orders.o_orderpriority, orders.o_comment, MIN(orders.o_orderkey) AS min_orders_o_orderkey, VARIANCE(orders.o_orderkey) AS variance_orders_o_orderkey FROM orders WHERE ((orders.o_orderstatus = 'GWVHH') OR (orders.o_clerk = 'YDIQV')) OR (orders.o_orderstatus LIKE '%B%') AND orders.o_orderkey BETWEEN 676 AND 708 GROUP BY orders.o_orderpriority, orders.o_comment HAVING COUNT(*) > 1756 AND VARIANCE(orders.o_orderkey) > 25733 ORDER BY orders.o_orderpriority, orders.o_comment