SELECT nation.n_comment, MAX(nation.n_nationkey) AS max_n_nationkey, MIN(nation.n_regionkey) AS min_n_regionkey, COUNT(nation.n_regionkey) AS count_n_regionkey FROM nation WHERE nation.n_regionkey < 549 AND nation.n_comment = 'DOHUX' AND nation.n_name = 'CVUFU' AND nation.n_comment = 'KSZMK' AND nation.n_comment LIKE 'A%' GROUP BY nation.n_comment HAVING COUNT(*) > 188 ORDER BY nation.n_comment